import {Component, OnInit} from '@angular/core';
import {ActivatedRoute, Router} from "@angular/router";
import {TontineServiceService} from "../../Services/tontine-service.service";
import {Cotisation} from "../../../Models/Entitys/Cotisation";
import {Tontine} from "../../../Models/Abstracts/Tontine";

@Component({
  selector: 'app-seance-list',
  templateUrl: './seance-list.component.html',
  styleUrls: ['./seance-list.component.scss']
})
export class SeanceListComponent implements OnInit{

  displayedColumns: string[] = ['intitule', 'seance', 'tontine'];
  dataSource : Cotisation[] = [];
  jsonTon="";
  idTontine!:number;
  nextRoute:string ="/public/tontine/affiche/";
  tontine!:Tontine
  constructor(private router:ActivatedRoute,
              private route:Router,
              private tontineService:TontineServiceService) {
  }
  ngOnInit() {
    if(this.route.url.slice(0,22)=="/public/echec/seances/"){
      this.nextRoute="/public/echec/seances/";
    }
    this.idTontine=this.router.snapshot.params["id"];
    this.jsonTon=this.router.snapshot.params["ton"];
    this.tontine=JSON.parse(this.jsonTon);
    this.router.snapshot.params
        this.tontineService.getCotisation(this.idTontine).subscribe(
          data =>{
            this.dataSource = data;
            this.dataSource.sort((a, b) => {
              if (a.rang>b.rang)
                return -1;
              else if (a.rang<b.rang)
                return 1;
              else
                return 0;
            });
          });

  }

  endCotisation(cot:Cotisation){
    cot.statut=3;
    cot.tontine=this.tontine;
    this.tontineService.saveCotisation(cot).subscribe(
      data =>{
        console.log("end",data);
      }
    );
  }

  stringify(cot:Cotisation):string{

    return JSON.stringify(cot);
  }

}
<div class="content">
  <div class="tab">
    <span class="pan">
      <h4> TONTINE VENTE  </h4>
    </span>
    <table mat-table [dataSource]="dataSource" class="mat-elevation-z8">

      <!--- Note that these columns can be defined in any order.
            The actual rendered columns are set as a property on the row definition" -->

      <!-- Position Column -->
      <ng-container matColumnDef="intitule">
        <th mat-header-cell *matHeaderCellDef> Intitule </th>
        <td mat-cell *matCellDef="let element"> {{element.nom}} </td>
      </ng-container>

      <!-- Name Column -->
      <ng-container matColumnDef="seance">
        <th mat-header-cell *matHeaderCellDef> seance </th>
        <td mat-cell *matCellDef="let element"> {{element.rang}} </td>
      </ng-container>

      <!-- Weight Column -->
      <ng-container matColumnDef="tontine">
        <th mat-header-cell *matHeaderCellDef> Cotisation </th>
        <td mat-cell *matCellDef="let element">
          <span>
            <button mat-stroked-button color="primary" routerLink="{{nextRoute}}{{idTontine}}/{{element.id}}" [queryParams]="{ton:jsonTon,cot:stringify(element)}" *ngIf="element.statut >=2"> Afficher </button>
            <button mat-flat-button color="primary" (click)="endCotisation(element)" *ngIf="element.statut <2"> Terminer </button>
          </span>
        </td>
      </ng-container>

      <tr mat-header-row *matHeaderRowDef="displayedColumns"></tr>
      <tr mat-row *matRowDef="let row; columns: displayedColumns;"></tr>

    </table>
  </div>
</div>
