import {Component, CUSTOM_ELEMENTS_SCHEMA, OnInit} from '@angular/core';
import {MatCardModule} from "@angular/material/card";
import {MatButtonModule} from "@angular/material/button";
import {MatTableModule} from "@angular/material/table";
import {MatListModule} from "@angular/material/list";
import {MatFormFieldModule} from "@angular/material/form-field";
import {MatInputModule} from "@angular/material/input";
import {MatSelectModule} from "@angular/material/select";
import {MatTabsModule} from "@angular/material/tabs";
import {MatGridListModule} from "@angular/material/grid-list";
import {MembreServiceService} from "../../../Services/membre-service.service";
import {Membre} from "../../../../Models/Entitys/Membre";
import {DatePipe, NgForOf, NgIf} from "@angular/common";
import {MatAutocompleteModule} from "@angular/material/autocomplete";
import {FormBuilder, FormControl, FormsModule, ReactiveFormsModule} from "@angular/forms";
import {ActivatedRoute, RouterModule} from "@angular/router";
import {Epargne} from "../../../../Models/Entitys/Epargne";
import {EpargneService} from "../../../Services/epargne.service";
import {Epargnant} from "../../../../Models/Entitys/Epargnant";
import {Depot} from "../../../../Models/Entitys/Depot";
import {MatRadioModule} from "@angular/material/radio";
import {Tontine} from "../../../../Models/Abstracts/Tontine";
import {TontineServiceService} from "../../../Services/tontine-service.service";
import {Garrantie} from "../../../../Models/Entitys/Garrantie";
import {Pret} from "../../../../Models/Entitys/Pret";
import {TypeEpargne} from "../../../../Models/Entitys/TypeEpargne";
import {AuthService} from "../../../Services/auth.service";


export interface gage{
  type:number;
  epargnantId:number|null;
  tontineId:number|null;
  autres:string;
}
export interface epar{
  epargnant:Epargnant|null;
  capital:number;
  dette:number;
}



@Component({
  selector: 'app-etat',
  templateUrl: './caisse-etat.component.html',
  styleUrls: ['./caisse-etat.component.scss'],
  standalone: true,

  imports: [
    MatCardModule,
    MatButtonModule,
    MatTableModule,
    MatListModule,
    MatFormFieldModule,
    MatInputModule,
    MatSelectModule,
    MatTabsModule,
    MatGridListModule,
    NgForOf,
    MatAutocompleteModule,
    FormsModule,
    NgIf,
    ReactiveFormsModule,
    RouterModule,
    DatePipe,
    MatRadioModule,
  ],
  schemas:[CUSTOM_ELEMENTS_SCHEMA]
})
export class CaisseEtatComponent implements OnInit{
  displayedColumns: string[] = ['position', 'name', 'weight', 'symbol'];
  dataSource:Epargnant[] = [];
  associationMembers:Membre[]=[];
  dataS:Membre[]=[];
  epargne!:Epargne;
  table:Depot[][]=[]
  capitales:number[]=[];
  nbrowSpan=0;
  emprunteur!:Epargnant;
  emprunteurId!:number;
  montantPret:number=0;
  delais:number=0;
  tontinesGageble:Tontine[]=[];
  garranties:gage[]=[
    {type:2,epargnantId:null,tontineId:null,autres:""}
  ];
  capitaux:epar[]=[];


  formDep=this.formBuilder.group({
    membre:new FormControl(),
    montant:new FormControl<number>(0),
    montantPret:new FormControl<number>(0),
    delais:new FormControl<number>(0),
  })
  index:number =0;


  constructor(private memberService :MembreServiceService,
              private epargneServ:EpargneService,
              private formBuilder:FormBuilder,
              private activatedRoute: ActivatedRoute,
              private tontineService:TontineServiceService,
              public authService:AuthService) {
  }

  ngOnInit() {
    var  snapshot = this.activatedRoute.snapshot;
    this.epargne = JSON.parse(snapshot.params["ep"]);
    console.log("ep",this.epargne);
    this.capitaux=[];
    this.memberService.LoadMembres().subscribe(
      data=>{
        this.associationMembers = data;
        this.dataS=data;
      }
    );
    let prets:Pret[]=[];
    if (this.epargne.id){
      this.epargneServ.getAllDepoByEpargnes(this.epargne.id).subscribe(
        data=>{

          this.epargneServ.getAllEmpruntByEpargnes(Number(this.epargne.id)).subscribe(
            dataP=>{
              console.log("prets",dataP);
              prets=dataP;


              console.log("data",data);
              let isAdd=false;
              let tabLength=0,tabJLength=0;

              console.log(this.table);
              // Construction d'un tableau a deux dimention groupant les depot d'un meme epargnant sur une meme ligne
              for (let i=0;i<data.length;i++){
                for (let j=0;j<this.table.length;j++){
                  tabJLength=this.table[j].length;
                  for (let k=0;k<tabJLength;k++){
                    if(data[i].epargnant && data[i].epargnant?.id == this.table[j][k].epargnant?.id){
                      if(!this.table[j])
                        this.table[j]=[];
                      this.table[j].push(data[i]);
                      isAdd=true;
                    }
                  }
                }
                if(!isAdd){
                  this.table.push([data[i]]);
                }
                isAdd=false;
              }

              let montant=0;
              let k=0,n=1,m=0;
              // for(let i=0 ;i<200;i++)
              for (let tab of this.table){
                k++;
                this.dataSource.push(<Epargnant>tab[0].epargnant);
                for (let dep of tab){
                  montant+=Number(dep.montant);
                }
                this.capitales.push(montant);
                this.capitaux.push({epargnant:<Epargnant>tab[0].epargnant,capital:montant,dette:0})
                montant=0;
                if (k==10){
                  k=0;
                  n++;
                  n%5==0?m++:m+=0;
                }
              }

              for(let prt of prets){
                for (let i=0;i<this.capitaux.length;i++){
                  if(prt.epargnant?.id==this.capitaux[i].epargnant?.id){
                    console.log("ep",prt);
                    this.capitaux[i].dette+=(Number(prt.montant)*this.epargne.tauxInteretPret/100)+Number(prt.montant);
                  }
                }
              }
              console.log("dataSource:",this.dataSource);


              console.log("tab:",this.table);
              console.log("cap:",this.capitaux);



              this.nbrowSpan = this.dataSource.length+(7*n)-(7*m);



            }
          );


        }
      );


    }


  }

  deposit(){

    if(this.index==1){
      let epargnant = new Epargnant();
      let depo = new Depot();
      let Membre:string
      epargnant.dateEnregistrement=new Date();
      epargnant.epargne=this.epargne;
      Membre=String(this.formDep.value.membre);

      console.log("membre",Membre);
      for (let membre of this.dataS){
        if(membre.nom==Membre)
          epargnant.membre=membre;
      }
      for(let ep of this.dataSource){
        if(Membre==ep.membre?.nom){
          epargnant=ep;
        }
      }

      depo.epargnant=epargnant;
      depo.montant=Number(this.formDep.value.montant);
      depo.dateDepot=new Date();

      console.log("depot",depo);

      this.epargneServ.Deposit(depo).subscribe(
        data=>{
          console.log("save",data);

          this.dataSource = [];
          this.associationMembers=[];
          this.dataS=[];
          this.table=[]
          this.capitales=[];
          this.nbrowSpan=0;

          this.ngOnInit();
        }
      )

    }
    // save emprunt
    else{
      let garrantie:Garrantie = new Garrantie();
      let tontines:Tontine[]=[];
      console.log("gage", this.garranties);
      this.tontineService.LoadTontine().subscribe(data=>{
        tontines = data;

        for(let gage of this.garranties){
          if(gage.epargnantId)
             garrantie.epargnants.push(this.emprunteur);
          if(gage.tontineId)
            for (let ton of tontines)
              if(ton.id==gage.tontineId)
                garrantie.tontines.push(ton);
          if(gage.autres!="")
            garrantie.autres+="/new/"+gage.autres;
        }
        let emprunt:Pret = new Pret();

        emprunt.garrantie=garrantie;
        emprunt.montant=Number(this.formDep.value.montantPret);
        let type =<TypeEpargne>this.epargne.type;
        emprunt.echeanche=type.code==2?Number(this.formDep.value.delais):Number(this.epargne.delaisRemboursement);
        emprunt.datePret=new Date();
        emprunt.motif="";
        emprunt.epargnant=this.emprunteur;


        this.epargneServ.Emprunt(emprunt).subscribe(data=>{
          console.log("emprunt",data);

          this.ngOnInit();

        });

      });


    }

  }

  applyFilter(event: Event) {
    const filterValue = (event.target as HTMLInputElement).value;
    console.log(filterValue.trim().toLowerCase());
    this.dataS=this.associationMembers;
    this.dataS = this.dataS.filter((obj)=>{

      if(filterValue.trim().toLowerCase().length==0)
        return obj.nom.length!=0;
      return obj.nom.toLowerCase().includes(filterValue.trim().toLowerCase());
    })
  }


  getCaisseMontant(){
    let total=0;
    for(let montant of this.capitales){
      total+=montant;
    }
    return total-this.getPretMontant();
  }
  getPretMontant(){
    let total=0;
    for(let montant of this.capitaux){
      total+=montant.dette;
    }
    return total;
  }

  addGage(){
    this.garranties.push({type:2,epargnantId:null,tontineId:null,autres:""});
  }


  btnIsDiable(){
    if(this.index==0){
      let i=0,j=0;
      for (i=0;i<this.capitaux.length;i++){
        if(<Epargnant>this.capitaux[i].epargnant==this.emprunteur)
          j=i;
      }
      if(this.emprunteur && Number(this.formDep.value.montantPret)<=(this.epargne.coefPret*this.capitaux[j].capital) && Number(this.formDep.value.montantPret)<=this.getCaisseMontant()){
        if(this.garranties[0].tontineId||this.garranties[0].epargnantId||this.garranties[0].autres!="")
          return true;
      }
      return false;
    }else {
      if (this.formDep.value.membre && this.formDep.value.montant && this.formDep.value.montant>0)
        return true;

      return false;
    }
  }


  checkTontine(){
    this.garranties=[
      {type:2,epargnantId:null,tontineId:null,autres:""}
    ];
    if(this.emprunteurId){
      for (let ep of this.dataSource){
        if(this.emprunteurId==ep.id)
          this.emprunteur=ep;
      }
      console.log(this.emprunteur);
      if(this.emprunteur.membre)
      this.tontineService.MyTontine(this.emprunteur.membre.id).subscribe(data=>{
        this.tontinesGageble=data;
      });
    }
  }
}
<div class="content">
  <mat-grid-list cols="10" class="main" rowHeight="5vh">

    <mat-grid-tile colspan="7" rowspan="5" class="main--wrapper">

      <mat-card class="main--card">
        <mat-card-header>
          <mat-grid-list cols="3" rowHeight="6vh" class="info">
            <mat-grid-tile colspan="3">
              <mat-card-title>{{epargne.nom}}</mat-card-title>
            </mat-grid-tile>
            <mat-grid-tile>
              <mat-card-subtitle>Cassation le {{epargne.dateCassation|date:"yyyy-MM-dd"}} </mat-card-subtitle>
            </mat-grid-tile>
            <mat-grid-tile>
              <mat-card-subtitle>Plafond de pret: capital X{{epargne.coefPret}}</mat-card-subtitle>
            </mat-grid-tile>
            <mat-grid-tile>
              <mat-card-subtitle>Cree le {{epargne.dateCreation|date:"yyyy-MM-dd"}} </mat-card-subtitle>
            </mat-grid-tile>
            <mat-grid-tile>Taux d'intert sur pret : {{epargne.tauxInteretPret}}%</mat-grid-tile>
            <mat-grid-tile>
              <mat-card-title>Caisse: {{getCaisseMontant()}} F </mat-card-title>
            </mat-grid-tile>
            <mat-grid-tile>
              <mat-card-title>Dettes: {{getPretMontant()}} F </mat-card-title>
            </mat-grid-tile>

<!--            <mat-grid-tile></mat-grid-tile>-->
<!--            <mat-grid-tile></mat-grid-tile>-->
<!--            <mat-grid-tile></mat-grid-tile>-->

          </mat-grid-list>

          <mat-card-subtitle></mat-card-subtitle>
<!--          <mat-card-title>{{getCaisseMontant()}}</mat-card-title>-->
<!--          <mat-card-subtitle>A Rembourser</mat-card-subtitle>-->

        </mat-card-header>
        <mat-card-content>
        </mat-card-content>

      </mat-card>

    </mat-grid-tile>

    <mat-grid-tile colspan="3" rowspan="13" class="rigth--form--wapper">


    </mat-grid-tile>

    <mat-grid-tile colspan="7" [rowspan]="nbrowSpan" class="table--wrapper">


      <mat-card class="table--card">
        <mat-card-header>
          <mat-card-title>Liste des epargnants</mat-card-title>
          <mat-card-subtitle>...</mat-card-subtitle>


        </mat-card-header>
        <mat-card-content>

          <table  mat-table [dataSource]= "capitaux.slice()"  class = "mat-elevation-z8" >

            <!--- Notez que ces colonnes peuvent être définies dans n'importe quel ordre.
                  Les colonnes réellement rendues sont définies en tant que propriété dans la définition de ligne" -->

            <!-- Position Column -->
            <ng-container  matColumnDef = "position">
              <th  mat-header-cell *matHeaderCellDef > No. </th >
              <td  mat-cell *matCellDef = "let element, index as i" > {{i+1}} </td>
            </ng-container>

            <!-- Nom Colonne -->
            <ng-container  matColumnDef = "name">
              <th  mat-header-cell *matHeaderCellDef > Epargnants </th>
              <td  mat-cell *matCellDef = "let element" > {{element.epargnant?.membre.nom}} </td>
            </ng-container>

            <!-- Colonne de poids -->
            <ng-container  matColumnDef = "weight">
              <th  mat-header-cell *matHeaderCellDef> Epargnes </th>
              <td  mat-cell *matCellDef = "let element, index as i">{{element.capital}}</td>
            </ng-container>

            <!-- Symbol Column -->
            <ng-container  matColumnDef = "symbol">
              <th  mat-header-cell *matHeaderCellDef> Dettes </th>
              <td  mat-cell *matCellDef = "let element">{{element.dette}}  </td>
            </ng-container>

            <tr  mat-header-row *matHeaderRowDef = "displayedColumns"> </tr>
            <tr  mat-row *matRowDef="let row; columns: displayedColumns;"> </tr>
          </table>

        </mat-card-content>

      </mat-card>

    </mat-grid-tile>




  </mat-grid-list>


  <div class="pan" *ngIf="authService.role">
    <mat-card class="pret--card">
      <mat-card-header>
        <mat-card-subtitle>Effectuer une Operation</mat-card-subtitle>
        <mat-card-title>Operation</mat-card-title>

      </mat-card-header>
      <mat-card-content>
        <mat-form (submit)="deposit()" [formGroup]="formDep">
          <mat-tab-group [(selectedIndex)]="index">
            <mat-tab label="Pret">
              <div class="pret--wrapper">
                <mat-grid-list cols="4" rowHeight="85px" >

                  <mat-grid-tile colspan="4">
                    <mat-form-field floatLabel="always" appearance="outline">
                      <mat-label>Membre</mat-label>
                      <mat-select required [(ngModel)]="emprunteurId" [ngModelOptions]="{standalone: true}" (ngModelChange)="checkTontine()">
                        <mat-option *ngFor="let epargnant of dataSource" value="{{epargnant.id}}">{{epargnant.membre?.nom}}</mat-option>
                      </mat-select>
                    </mat-form-field>
                  </mat-grid-tile>


                  <mat-grid-tile colspan="4">
                    <mat-form-field floatLabel="always" appearance="outline">
                      <mat-label>Montant</mat-label>
                      <input matInput type="number" class="example-right-align" placeholder="0" formControlName="montantPret">
                    </mat-form-field>
                  </mat-grid-tile>

                  <mat-grid-tile colspan="4" *ngIf="epargne.type?.code==2">
                    <mat-form-field appearance="outline" floatLabel="always">
                      <mat-label>delais</mat-label>
                      <input matInput  type="number"  class="example-right-align" placeholder="" formControlName="delais" required>
                      <span matTextSuffix>Mois</span>
                    </mat-form-field>
                  </mat-grid-tile>

                  <mat-grid-tile *ngFor="let gage of garranties" colspan="4" rowspan="2">

                    <div class="garrantie--element">
                      <mat-label>type garrantie</mat-label>

                      <mat-radio-group aria-label="Select an option" [(ngModel)]="gage.type" [ngModelOptions]="{standalone: true}">
                        <mat-radio-button value=1>Tontine</mat-radio-button>
                        <mat-radio-button value=2>Membre</mat-radio-button>
                        <mat-radio-button value=3>Autre</mat-radio-button>
                      </mat-radio-group>


                      <mat-form-field *ngIf="gage.type==1" floatLabel="always" appearance="outline">
                        <mat-label>Tontine</mat-label>
                        <mat-select required [(ngModel)]="gage.tontineId" [ngModelOptions]="{standalone: true}">
                          <mat-option *ngFor="let tontine of tontinesGageble" value="{{tontine.id}}">{{tontine.nom.slice(15)}}</mat-option>
                        </mat-select>
                      </mat-form-field>

                      <mat-form-field *ngIf="gage.type==2" floatLabel="always" appearance="outline">
                        <mat-label>Membre</mat-label>
                        <mat-select required [(ngModel)]="gage.epargnantId" [ngModelOptions]="{standalone: true}">
                          <mat-option *ngFor="let epargnant of dataSource" value="{{epargnant.id}}">{{epargnant.membre?.nom}}</mat-option>
                        </mat-select>
                      </mat-form-field>

                      <mat-form-field *ngIf="gage.type==3" appearance="outline">
                        <mat-label>Autre</mat-label>
                        <input matInput  type="text" placeholder="Garrantie" [(ngModel)]="gage.autres" [ngModelOptions]="{standalone: true}">
                      </mat-form-field>

                    </div>
                  </mat-grid-tile>

                  <mat-grid-tile colspan="4">
                    <button mat-flat-button color="primary" (click)="addGage()"> Ajouter une garrantie</button>
                  </mat-grid-tile>
                </mat-grid-list>
              </div>

            </mat-tab>
            <mat-tab label="Depot">
              <mat-grid-list cols="4" rowHeight="85px">

                <mat-grid-tile colspan="4">

                  <mat-form-field class="example-full-width" appearance="outline">
                    <mat-label>Membre</mat-label>
                    <input type="text"
                           placeholder="Membre"
                           aria-label="Number"
                           matInput
                           formControlName="membre"
                           (keyup)="applyFilter($event)"
                           [matAutocomplete]="auto">
                    <mat-autocomplete #auto="matAutocomplete">
                      <mat-option *ngFor="let member of dataS" [value]="member.nom">{{member.nom}}</mat-option>
                    </mat-autocomplete>

                  </mat-form-field>
                </mat-grid-tile>

                <mat-grid-tile colspan="4">
                  <mat-form-field floatLabel="always" appearance="outline">
                    <mat-label>Montant</mat-label>
                    <input matInput type="number" class="example-right-align" placeholder="0" formControlName="montant">
                  </mat-form-field>
                </mat-grid-tile>
              </mat-grid-list>

            </mat-tab>
          </mat-tab-group>
        </mat-form>

        <mat-divider></mat-divider>
      </mat-card-content>
      <mat-card-actions>
        <button mat-flat-button color="primary" (click)="deposit()" [disabled]="!btnIsDiable()">Confirmer</button>
      </mat-card-actions>
      <mat-card-footer>
      </mat-card-footer>
    </mat-card>
  </div>



</div>
