import {ChangeDetectionStrategy, Component} from '@angular/core';
import {AuthService} from "./Services/auth.service";

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.scss'],
  changeDetection:ChangeDetectionStrategy.OnPush
})
export class AppComponent {
  title = 'DanguiUi';

  constructor(public authService:AuthService) {
  }

}
<app-loader></app-loader>
<div class content>
  <span class="header-img" *ngIf="authService.isAuth">
    <span class="user--wapper">
      <label>{{authService.user}}</label>
      <label>Role: {{authService.role.slice(5)}}</label>
    </span>
    <img src="./assets/img/1.png" alt="Djangui">
  </span>
  <router-outlet></router-outlet>
  <div class="footer">
    <span>© 2023 Copyright: Djangui</span>
    <span>by MegaPlus</span>
  </div>
</div>
