import { Tontine } from "../Abstracts/Tontine";
import { Cotisation } from "./Cotisation";
import { GetTotalTontine } from "../../app/metier/getTotalTontine";
export class Simple extends Tontine {
  constructor(id, actif, nom, type, taux_tontine, taux_echec, taux_echec_b, taux_retard, frequence, created_at, updated_at, max, boisson) {
    super(id, actif, nom, type, taux_tontine, taux_echec, taux_retard, frequence, created_at, updated_at, max, taux_echec_b, Number(boisson));
  }
  getTodaySeance() {
    return new Cotisation(null, this.nom, 0, 0, this.tauxEchec, 0, 0, this.tauxTontine, 0, this, null, new Date(), new Date(), 0);
  }
  getTotals(allCotisation, allPart, allRetard, allEchecNonPaye, allEchecPaye) {
    return new GetTotalTontine().getTotalTontineSimple(allCotisation, allPart, allRetard, allEchecNonPaye, allEchecPaye, this);
  }
}
