import {ChangeDetectionStrategy, ChangeDetectorRef, Component, DestroyRef, inject, OnInit} from '@angular/core';
import {TontineServiceService} from "../../Services/tontine-service.service";
import {Tontine} from "../../../Models/Abstracts/Tontine";
import {TypeTontine} from "../../../Models/Entitys/TypeTontine";
import {AuthService} from "../../Services/auth.service";
import {DataService} from "../../Services/data.service";
import {takeUntilDestroyed} from "@angular/core/rxjs-interop";
import {switchMap} from "rxjs";

@Component({
  selector: 'app-showtontine',
  templateUrl: './tontine-bilan-list.component.html',
  styleUrls: ['./tontine-bilan-list.component.scss'],
    changeDetection:ChangeDetectionStrategy.OnPush,
})
export class TontineBilanListComponent implements OnInit{
  displayedColumns: string[] = ['position', 'name', 'cotisation','type','retard', 'echec'];
  dataSource:Tontine[]=[];
  types:TypeTontine[]=[];
  tontines:Array<Tontine[]>=[];
  Roles="";
  destroyRef = inject(DestroyRef);
  state:string[][]=[];
  constructor(private tontineService:TontineServiceService,private authSrv:AuthService,
              private dataService:DataService,private cd: ChangeDetectorRef) {

  }

  ngOnInit() {
    this.Roles=this.authSrv.role;
    this.tontineService.getTypes().pipe(
        takeUntilDestroyed(this.destroyRef),
        switchMap(types => {
            this.types=types;
            for (let type of this.types){
                this.tontines.push(new Array<Tontine>());
            }
            if(this.Roles.includes('BUREAU'))
                return this.tontineService.LoadTontine();
            return this.tontineService.LoadMyTontine();
        })
    ).subscribe(
          data=>{
            for(let i=0 ;i<data.length ;i++){
              for(let j=0;j<this.types.length;j++){
                if(data[i].type.id==this.types[j].id){
                  this.tontines[j].push(data[i]);
                }
              }
            }
            for (let i = 0; i < this.types.length; i++) {
                this.tontines[i].sort((a:Tontine , b:Tontine)=>{
                    if(a.actif && !b.actif)
                        return -1;
                    else if(!a.actif && b.actif)
                        return 1;

                    return 0;
                } );
            }

              this.cd.markForCheck();

          }
    );

  }

  getClass(tontine:Tontine ){
    return tontine.actif? "actif" : "inactif";
  }

  getTontine(j: number) {
    return this.tontines[j];
  }

    SelectTontine(element:Tontine) {
        this.dataService.actualTontine=element;
    }

    protected readonly JSON = JSON;
}
<div class="content">

  <div class="list" >
      <span class="pan">
      <h4> Liste Des Tontines</h4>
          <mat-tab-group animationDuration="750ms">
            <mat-tab *ngFor="let type of types ; let j=index" label="Tontines {{type.nom}}">
              <table mat-table [dataSource]="tontines[j].slice()" class="mat-elevation-z8">

                <!--- Note that these columns can be defined in any order.
                      The actual rendered columns are set as a property on the row definition" -->

                                  <!-- Position Column -->
                <ng-container matColumnDef="position">
                  <th mat-header-cell *matHeaderCellDef> No. </th>
                  <td mat-cell *matCellDef="let element,index as i " class="{{getClass(element)}}"> {{i+1}} </td>
                </ng-container>

                                  <!-- Name Column -->
                <ng-container matColumnDef="name">
                  <th mat-header-cell *matHeaderCellDef> Nom </th>
                  <td mat-cell *matCellDef="let element" class="{{getClass(element)}}"> {{element.nom}} </td>
                </ng-container>

                                  <!-- Weight Column -->
                <ng-container matColumnDef="cotisation">
                  <th mat-header-cell *matHeaderCellDef> Taux </th>
                  <td mat-cell *matCellDef="let element" class="{{getClass(element)}}">
                    <span class="lab">{{element.tauxTontine}}</span>
                  </td>
                </ng-container>

                <ng-container matColumnDef="type">
                  <th mat-header-cell *matHeaderCellDef> Type </th>
                  <td mat-cell *matCellDef="let element" class="{{getClass(element)}}">
                    <span class="lab">{{element.type.nom}}</span>
                  </td>
                </ng-container>

                                  <!-- Symbol Column -->
                <ng-container matColumnDef="retard">
                  <th mat-header-cell *matHeaderCellDef> </th>
                  <td mat-cell *matCellDef="let element" class="{{getClass(element)}}"> <button mat-flat-button color="warn" routerLink="/public/tontine/desc/{{element.id}}" (click)="SelectTontine(element)">Afficher</button> </td>
                </ng-container>

                <ng-container matColumnDef="echec">
                  <th mat-header-cell *matHeaderCellDef> </th>
                  <td mat-cell *matCellDef="let element" class="{{getClass(element)}}"> <button *ngIf="element.actif && this.Roles.includes('BUREAU')" mat-stroked-button disabled="false" routerLink="/public/member/add/{{element.id}}" color="primary">Ajouter un Membre</button>
                  </td>
                </ng-container>

                <tr mat-header-row *matHeaderRowDef="displayedColumns"></tr>
                <tr mat-row *matRowDef="let row; columns: displayedColumns;"></tr>

              </table>
            </mat-tab>
<!--            <mat-tab label="Tontines Simples">Content 2</mat-tab>-->
          </mat-tab-group>


    </span>
  </div>
</div>
