import {ChangeDetectorRef, Component} from '@angular/core';
import {FormBuilder, FormControl} from "@angular/forms";
import {Epargne} from "../../../../Models/Entitys/Epargne";
import {MatSnackBar} from "@angular/material/snack-bar";
import {EpargneService} from "../../../Services/epargne.service";
import {TypeEpargne} from "../../../../Models/Entitys/TypeEpargne";
import {Router} from "@angular/router";

@Component({
  selector: 'app-create-caisse',
  templateUrl: './create-caisse.component.html',
  styleUrls: ['./create-caisse.component.scss']
})
export class CreateCaisseComponent {

  Form=this.formBuilder.group({
    intituler:"",
    type:"",
    dateCassation:new FormControl(new Date()),
    coefPret:new FormControl(0),
    tauxInteretPret:new FormControl(0),
    minDep:new FormControl(0),
    basePret:new FormControl(0),
    delaisRemboursement:new FormControl(0),
  });

  type:number|null = null;
  constructor(private formBuilder:FormBuilder,
              private snackBar: MatSnackBar,
              private epargneServ: EpargneService,
              private router:Router,
              private cdr:ChangeDetectorRef) {
  }


  openSnackBar(message: string, action: string) {
    this.snackBar.open(message, action ,{
      duration:3000
    });
  }

  creatEpargne(){
    let epargne:Epargne = new Epargne();

    epargne.nom=String(this.Form.value.intituler);
    epargne.dateCreation=new Date();
    epargne.coefPret=Number(this.Form.value.coefPret);
    epargne.dateCassation = this.Form.value.dateCassation?this.Form.value.dateCassation:null;
    epargne.delaisRemboursement=Number(this.Form.value.delaisRemboursement);
    epargne.minDep=Number(this.Form.value.minDep);
    epargne.tauxInteretPret=Number(this.Form.value.tauxInteretPret);

    let typeEpargne:TypeEpargne = this.type==1?new TypeEpargne(1,"Banque Scolaire",1):new TypeEpargne(2,"Emergence",2);
    epargne.type=typeEpargne;
    console.log("epargne",epargne);

    this.epargneServ.saveEpargne(epargne).subscribe(data=>{
      console.log(data);
      this.openSnackBar("'"+epargne?.nom?.toUpperCase()+"' Cree avec succes !" ,"Continuer");
      this.router.navigateByUrl("/public/Caisse/depot");
    });
  }
}

<div class="content">

  <div class="list">



    <form [formGroup]="Form" (ngSubmit)="creatEpargne()">

      <mat-form-field appearance="outline">
        <mat-select id="type"  required placeholder="Type" formControlName="type" [(ngModel)]="type">
          <mat-option value=1>Banque Scolaire</mat-option>
          <mat-option value=2>Emergance</mat-option>
        </mat-select>
      </mat-form-field>

      <mat-form-field appearance="outline">
        <mat-label>Nom:</mat-label>
        <input matInput id="intituler" type="text" placeholder="Intitulé:" formControlName="intituler" required>
      </mat-form-field>

      <mat-form-field appearance="outline">
        <mat-label>Montant Minimum de depot</mat-label>
        <input matInput id="tauxTontine" type="number" placeholder="Montant" formControlName="minDep" required>
        <span matTextSuffix>Fcfa</span>
      </mat-form-field>

      <mat-form-field appearance="outline">
        <mat-label>Plafond de pret :</mat-label>
        <input matInput id="coefPret" type="number" formControlName="coefPret" required>
        <span matTextPrefix>Capital X  </span>
      </mat-form-field>

      <mat-form-field appearance="outline" *ngIf="type && type==1">
        <mat-label>delais de remboursement</mat-label>
        <input matInput id="nbSeances" type="number" placeholder="nombre de seances" formControlName="delaisRemboursement" required>
      </mat-form-field>

      <mat-form-field appearance="outline">
        <mat-label>Taux d'interet sur les prets</mat-label>
        <input matInput id="frequence" type="number" placeholder="Interet pret" formControlName="tauxInteretPret" required>
        <span matTextSuffix>%</span>
      </mat-form-field>



      <mat-form-field appearance="outline">
        <mat-label>Date de cassation</mat-label>
        <input matInput formControlName="dateCassation" [matDatepicker]="picker" required>
        <mat-datepicker-toggle matIconSuffix [for]="picker"></mat-datepicker-toggle>
        <mat-datepicker #picker></mat-datepicker>
      </mat-form-field>

      <button mat-raised-button color="primary" type="submit" [disabled]="!Form.valid">Créer</button>
    </form>

  </div>

</div>
