import {Component, OnInit} from '@angular/core';
import {Cotisation} from "../../../../Models/Entitys/Cotisation";
import {ActivatedRoute} from "@angular/router";
import {TontineServiceService} from "../../../Services/tontine-service.service";
import {Tontine} from "../../../../Models/Abstracts/Tontine";
import {BilanServiceService} from "../../../Services/bilan-service.service";
import {forkJoin} from "rxjs";
import {AuthService} from "../../../Services/auth.service";
import jsPDF from "jspdf";
import autoTable from "jspdf-autotable";
import {DataService} from "../../../Services/data.service";
import {Vente} from "../../../../Models/Entitys/Vente";
import {TontinesOps} from "../../../../Models/Abstracts/Interfaces/TontinesOps";


@Component({
  selector: 'app-tontine-bilan',
  templateUrl: './tontine-bilan.component.html',
  styleUrls: ['./tontine-bilan.component.scss']
})
export class TontineBilanComponent implements OnInit{


  displayedColumns: string[] = ['S','Date','Cot.','T.V.','Comp.','Supl.','Boi.','Ret.','E.P.','E.N.P.','bt'];
  displayedSimpleColumns: string[] = ['S','Date','Cot.','Comp.','Ret.','E.P.','E.N.P.','bt'];
  dataSource : Cotisation[] = [];
  nbParts:number[][]=[];
  nbRetards:number[][]=[]
  nbPaidEchec:number[][]=[];
  nbNonPaidEchec:number[][]=[];
  idTontine!:number;
  nom:string="";
  nbToureGratuit:number=0;
  totalRetrad =0 ;totalEchecPaid =0; TotalComplement  = 0;
  totalTontine =0; totalEchecNonPaid=0;
  total=0;
  totalBoisson=0;
  totalGratuit=0;
  displayedFooterColumns= ['label','total','totalComplement','totalSuplement','boisson','totalRt','totalE.P','totalE.N.P','space'];
  displayedFooterSimpleColumns= ['label','total','totalComplement','totalRt','totalE.P','totalE.N.P','space'];
  tontine!:Tontine;
  type="Vente";
  lableColspan=3;
  totalSupplement=0;
  role="";
  tontineOperable!:TontinesOps;

  constructor(
    private router:ActivatedRoute,private tontineService:TontineServiceService,
    private bilanServ:BilanServiceService,private authserv:AuthService,
    private dataService:DataService) {
  }
  ngOnInit() {

    this.idTontine=this.router.snapshot.params["id"];
    this.role=this.authserv.role;
    // this.tontine=JSON.parse(this.router.snapshot.queryParams["ton"]);
    this.tontine=this.dataService.actualTontine;
    console.log("############",this.tontine);
    let cotsCall=this.tontineService.getCotisation(this.idTontine);
    let nbCotsCall = this.bilanServ.getNbCotisation(this.idTontine);

    let nbRetardCall = this.bilanServ.getNbRetard(this.idTontine);

    let nbPaidCall = this.bilanServ.getNbPaidEchec(this.idTontine);

    let nbNonPaidsCall = this.bilanServ.getNbNonPaidEchec(this.idTontine);

    forkJoin([cotsCall,nbCotsCall,nbRetardCall,nbPaidCall,nbNonPaidsCall]).subscribe(data=>{
      this.dataSource = data[0];
      if(this.dataSource.length>0)
        this.nom=data[0][0].nom;
      for (let cot of data[0]){
        if(cot.gratuit)
          this.nbToureGratuit++;
      }
      this.nbParts = data[1];
      this.nbRetards = data[2];
      this.nbPaidEchec =data[3];
      this.nbNonPaidEchec =data[4];

      this.type=this.tontine.type.nom;
      if(this.type=="Vente"){
        console.log("Vente")
        this.lableColspan=3;
        this.tontineOperable = this.tontine.buildVente();
      }
      if (this.type=="Simple"){
        this.lableColspan=2;
        this.tontineOperable = this.tontine.buildSimple();
      }

    })
  }

  ngDoCheck() {
    // this.total=this.getTotal();

  }


  getTableHeader(){
    if (this.type=="Vente")
      return this.displayedColumns;
    else
      return this.displayedSimpleColumns;
  }
  getTableFooter(){
    if (this.type=="Vente")
      return this.displayedFooterColumns;
    else
      return this.displayedFooterSimpleColumns;
  }

  // renvoi le nombre de participation qu'il y'a eu a une cotisation
  nbPart(idCot:number,rang:number){
    let cot =this.dataSource[rang-1];
    for(let part of this.nbParts ){

      if(part[0]==idCot){
        return part[1];
      }
    }
    return 0;
  }

  nbPaid(idCot:number,rang:number){
    let cot =this.dataSource[rang-1];
    for(let echec of this.nbPaidEchec ){

      if(echec[0]==idCot){
        return echec[1]*(cot.tauxEchec+cot.tauxTontine);
      }
    }
    return 0;
  }

  // renvoi le suplement a une cotisation
  getSupl(rang:number){

    let cot = this.dataSource[rang-1];
    let nbcotise = this.nbPart(Number(cot.id),rang);
    let dif= nbcotise-this.tontine.nbSeances;
    if (dif<=0)
      return 0;

    return dif*this.tontine.tauxTontine;

  }
  nbNonPaid(idCot:number,rang:number){
    let cot :Cotisation= this.dataSource[rang-1];
    for(let ct of this.dataSource){
      if(ct.id==idCot)
        cot=ct;
    }
    // console.log(rang);
    for(let echec of this.nbNonPaidEchec ){

      if(echec[0]==idCot){
        // console.log("echec",echec[1]*cot.tauxEchec);
        return echec[1]*cot.tauxEchec;

      }
    }
    // console.log(0);
    return 0;

  }

  nbRetard(idCot:number,rang:number){
    let cot =this.dataSource[rang-1];
    for(let retar of this.nbRetards ){

      if(retar[0]==idCot){
        if(this.type=="Vente")
          return retar[1]*cot.tauxEchec;
        if(this.type=="Simple")
          return retar[1]*this.tontine.tauxRetard;
      }
    }
    return 0;
  }

  getTotal(tontine:TontinesOps){
    return tontine.getTotals(this.dataSource,this.nbParts,this.nbRetards,this.nbNonPaidEchec,this.nbPaidEchec).get("total");
  }

  getClass(elm:Cotisation){
    if(elm.gratuit)
      return "gratuit";
    return "nonGratuit";
  }

  openOrClose() {
    this.tontine.actif= (!this.tontine.actif);
    this.tontineService.saveTontine(this.tontine).subscribe(
      data =>{
        console.log(data);
      }
    )
  }


  public openPDF(): void {

    var part=[];
    let i =0;
    const options = {
      startY: 20, // Vertical position to start the table (in mm)
      startX:20,
    };
    const doc = new jsPDF();
      doc.text("Bilan de la Tontine : "+String(this.tontine?.nom).slice(16), 10, 7);

    autoTable(doc,{html:'#htmlData',startY:21});


    doc.save('Tontine '+this.tontine?.nom+'.pdf');
  }
}
<div class="content">
  <div class="tab">
    <span class="pan">
      <h4> {{nom}} </h4>
      <mat-grid-list cols="3" rowHeight="35px">
        <mat-grid-tile><h4>taux :{{tontine.tauxTontine|money}}</h4></mat-grid-tile>
        <mat-grid-tile *ngIf="tontine?.type?.code==2"><h4>taux Echec B :{{tontine.tauxEchecB}}</h4></mat-grid-tile>
        <mat-grid-tile><h4>taux Echec :{{tontine.tauxEchec|money}}</h4></mat-grid-tile>
        <mat-grid-tile><h4>nombre max de part : {{tontine.nbSeances}}</h4></mat-grid-tile>
        <mat-grid-tile><h4>statu : {{tontine.actif? "Active" :"Fermer"}}</h4></mat-grid-tile>
        <mat-grid-tile><h4>cree le : {{tontine.createdAt|date:"jj-mm-yyyy"}}</h4></mat-grid-tile>

      </mat-grid-list>
    </span>
    <table mat-table [dataSource]="dataSource" class="mat-elevation-z8" id="htmlData">

      <!--- Note that these columns can be defined in any order.
            The actual rendered columns are set as a property on the row definition" -->

      <!-- Position Column -->
      <ng-container matColumnDef="S">
        <th mat-header-cell *matHeaderCellDef> S </th>
        <td mat-cell *matCellDef="let element" class="{{getClass(element)}}">
          <span class="mobile-label"> Seance :</span>
          {{element.rang}} </td>
      </ng-container>

      <!-- Name Column -->
      <ng-container matColumnDef="Date">
        <th mat-header-cell *matHeaderCellDef> Date </th>
        <td mat-cell *matCellDef="let element" class="{{getClass(element)}}">
          <span class="mobile-label"> Date :</span>

          {{element.createdAt}} </td>
      </ng-container>

      <ng-container matColumnDef="Cot.">
        <th mat-header-cell *matHeaderCellDef> Cot </th>
        <td mat-cell *matCellDef="let element" class="{{getClass(element)}}">
          <span class="mobile-label"> Cotisation :</span>
          {{nbPart(element.id,element.rang)}} </td>
      </ng-container>

      <ng-container matColumnDef="T.V." *ngIf="type=='Vente'">
        <th mat-header-cell *matHeaderCellDef> T.V. </th>
        <td mat-cell *matCellDef="let element" class="{{getClass(element)}}">
          <span class="mobile-label"> Taux Vente :</span>
          {{element.tauxVente|money}} </td>
      </ng-container>

      <ng-container matColumnDef="Comp.">
        <th mat-header-cell *matHeaderCellDef> Comp </th>
        <td mat-cell *matCellDef="let element" class="{{getClass(element)}}">
          <span class="mobile-label"> Complement :</span>
          {{element.complement|money}} </td>
      </ng-container>
      <ng-container matColumnDef="Supl." *ngIf="type=='Vente'">
        <th mat-header-cell *matHeaderCellDef> Supl </th>
        <td mat-cell *matCellDef="let element" class="{{getClass(element)}}">
          <span class="mobile-label"> Suplement :</span>
          {{getSupl(element.rang)}}</td>
      </ng-container>
      <ng-container matColumnDef="Boi." *ngIf="type=='Vente'">
        <th mat-header-cell *matHeaderCellDef> Boi </th>
        <td mat-cell *matCellDef="let element" class="{{getClass(element)}}">
          <span class="mobile-label"> Boisson :</span>
          {{element.tauxTontine}}</td>
      </ng-container>

      <ng-container matColumnDef="Ret.">
        <th mat-header-cell *matHeaderCellDef> Ret </th>
        <td mat-cell *matCellDef="let element" class="{{getClass(element)}}">
          <span class="mobile-label"> Retard :</span>
          {{nbRetard(element.id,element.rang)|money}} </td>
      </ng-container>

      <ng-container matColumnDef="E.P.">
        <th mat-header-cell *matHeaderCellDef> E.P. </th>
        <td mat-cell *matCellDef="let element" class="{{getClass(element)}}">
          <span class="mobile-label"> E.P :</span>

          {{nbPaid(element.id,element.rang)|money}} </td>
      </ng-container>

      <ng-container matColumnDef="E.N.P.">
        <th mat-header-cell *matHeaderCellDef> E.N.P </th>
        <td mat-cell *matCellDef="let element" class="{{getClass(element)}}">
          <span class="mobile-label"> E.N.P :</span>

          {{nbNonPaid(element.id,element.rang)|money}} </td>
      </ng-container>

      <!-- Weight Column -->
      <ng-container matColumnDef="bt">
        <th mat-header-cell *matHeaderCellDef>  </th>
        <td mat-cell *matCellDef="let element" class="{{getClass(element)}}">
          <span class="mobile-label"> </span>

          <span>
            <button mat-stroked-button color="primary" routerLink="/public/tontine/affiche/{{idTontine}}/{{element.id}}" *ngIf="element.statut >=2"> Afficher </button>
            <h5 class="primary" *ngIf="element.statut <2"> Non Terminer </h5>
          </span>
        </td>
      </ng-container>

      <ng-container matColumnDef="label" *ngIf="type=='Vente'">
        <td mat-footer-cell *matFooterCellDef colspan="3">

          <h4>TOTAL:</h4>
        </td>
      </ng-container>
      <ng-container matColumnDef="label" *ngIf="type=='Simple'">
        <td mat-footer-cell *matFooterCellDef colspan="2">

          <h4>TOTAL:</h4>
        </td>
      </ng-container>
      <ng-container matColumnDef="total">
        <td mat-footer-cell *matFooterCellDef>
          <span class="mobile-label"> {{type=='Vente'?"Total Vente:":"Total Cotisation"}}</span>

          <h4>{{totalTontine|money}}</h4>
        </td>
      </ng-container>
      <ng-container matColumnDef="totalComplement">
        <td mat-footer-cell *matFooterCellDef>
          <span class="mobile-label"> Total Complement:</span>

          <h4>{{TotalComplement|money}}</h4>
        </td>
      </ng-container>
      <ng-container matColumnDef="totalSuplement">
        <td mat-footer-cell *matFooterCellDef>
          <span class="mobile-label"> Total Supplement:</span>

          <h4>{{totalSupplement|money}}</h4>
        </td>
      </ng-container>
      <ng-container matColumnDef="boisson" *ngIf="type=='Vente'">
        <td mat-footer-cell *matFooterCellDef>
          <span class="mobile-label"> Total Boisson:</span>

          <h4 *ngIf="type=='Vente'">{{totalBoisson|money}}</h4>
        </td>
      </ng-container>
      <ng-container matColumnDef="totalRt">
        <td mat-footer-cell *matFooterCellDef>
          <span class="mobile-label"> Total Retard:</span>

          <h4>{{totalRetrad|money}}</h4>
        </td>
      </ng-container>
      <ng-container matColumnDef="totalE.P">
        <td mat-footer-cell *matFooterCellDef>
          <span class="mobile-label"> Total E.P:</span>

          <h4>{{totalEchecPaid|money}}</h4>
        </td>
      </ng-container>
      <ng-container matColumnDef="totalE.N.P">
        <td mat-footer-cell *matFooterCellDef>
          <span class="mobile-label"> Total E.N.P:</span>

          <h4>{{totalEchecNonPaid|money}}</h4>
        </td>
      </ng-container>

      <ng-container matColumnDef="space">
        <td mat-footer-cell *matFooterCellDef >
        </td>
      </ng-container>

      <tr mat-header-row *matHeaderRowDef="getTableHeader()"></tr>
      <tr mat-row *matRowDef="let row; columns: getTableHeader();"></tr>
      <tr mat-footer-row *matFooterRowDef="getTableFooter()"></tr>
    </table>

    <section class="pan">
    <span class="border-success" *ngIf="type=='Vente'">
            Total Caisse =  Tontine + Retard + Echec payé + supplement - Boisson - Complement - {{nbToureGratuit}} Tour(s) Gratuit
      </span>
        <span class="border-success" *ngIf="type=='Simple'">
            Total Caisse =  Tontine + Retard + Echec payé - Boisson - Complement
        </span>
      <h1>Total : {{getTotal()|money}} </h1>
    </section>
  </div>
<!--  <app-add-member></app-add-member>-->

  <app-classement> </app-classement>
  <div class="actions">
    <button mat-raised-button color="accent" (click)="openOrClose()" *ngIf="role.includes('BUREAU')">{{tontine.actif? "Fermer":"Ouvrire"}} cette tontine</button>
<!--    <button mat-raised-button color="accent" *ngIf="role.includes('BUREAU')" (click)="openPDF()"> Imprimer la liste</button>-->
  </div>
</div>
