import { Component } from '@angular/core';
import {MatDialogModule} from "@angular/material/dialog";
import {MatButtonModule} from "@angular/material/button";
import {MatInputModule} from "@angular/material/input";
import {FormBuilder, FormControl, ReactiveFormsModule} from "@angular/forms";
import {MatFormFieldModule} from "@angular/material/form-field";

@Component({
  selector: 'app-buy-pret-dialog',
  templateUrl: './buy-pret-dialog.component.html',
  standalone: true,
  imports: [
    MatDialogModule,
    MatButtonModule,
    MatFormFieldModule,
    MatInputModule,
    ReactiveFormsModule
  ],
  styleUrls: ['./buy-pret-dialog.component.scss']
})
export class BuyPretDialogComponent {
  form =this.fb.group({
    montant:new FormControl(0)
  })


  constructor(private fb:FormBuilder) {
  }

}
<form [formGroup]="form">
<div mat-dialog-content class="content">

    Confirmer le Versement de Chez Mr
<!--    <p>-->
