import {Component, OnInit} from '@angular/core';
import {animate, state, style, transition, trigger} from "@angular/animations";
import {DataService} from "../../Services/data.service";
import {Membre} from "../../../Models/Entitys/Membre";
import {MembreServiceService} from "../../Services/membre-service.service";
import {MatTableDataSource} from "@angular/material/table";
import {AuthService} from "../../Services/auth.service";

@Component({
  selector: 'app-member-list',
  templateUrl: './member-list.component.html',
  styleUrls: ['./member-list.component.scss'],
  animations: [
    trigger('detailExpand', [
      state('collapsed', style({height: '0px', minHeight: '0'})),
      state('expanded', style({height: '*'})),
      transition('expanded <=> collapsed', animate('225ms cubic-bezier(0.4, 0.0, 0.2, 1)')),
    ]),
  ],
  // standalone: true,
  // imports: [MatTableModule, NgFor, MatButtonModule, NgIf, MatIconModule],
})

export class MemberListComponent implements OnInit{

  mem:MatTableDataSource<Membre> = new MatTableDataSource<Membre>();
  constructor(private memberService :MembreServiceService,
              public authService:AuthService) {
  }

  ngOnInit() {
    this.memberService.LoadMembres().subscribe(
      data=>{
        let table:Membre[]=[];
        for (let el of data){
          if (el.nom!=="ADMIN"&&el.profession!=="Admin")
            table.push(el);
        }
        this.mem = new MatTableDataSource(table);
      }
    );
  }


  columnsToDisplay = ['No', 'Nom', 'Poste'];
  columnsToDisplayWithExpand = [...this.columnsToDisplay, 'expand'];
  expandedElement = this.mem.data[1];

  reset(element:Membre) {
    this.authService.reset(element).subscribe(data=>{});
  }
}
<div class="content">
  <span>Liste Membres</span>
  <div class="table">
    <table mat-table
           [dataSource]="mem" multiTemplateDataRows
           class="mat-elevation-z8">
      <ng-container matColumnDef="No">
        <th mat-header-cell *matHeaderCellDef>No </th>
        <td mat-cell *matCellDef="let element">{{element.idn}}</td>
      </ng-container>
      <ng-container matColumnDef="Nom">
        <th mat-header-cell *matHeaderCellDef>Nom </th>
        <td mat-cell *matCellDef="let element" class="nom">{{element.nom}}</td>
      </ng-container>
      <ng-container matColumnDef="Poste">
      <th mat-header-cell *matHeaderCellDef>Poste </th>
      <td mat-cell *matCellDef="let element">{{element.postes[0]?.nom}}</td>
    </ng-container>
      <ng-container matColumnDef="expand">
        <th mat-header-cell *matHeaderCellDef aria-label="row actions">&nbsp;</th>
        <td mat-cell *matCellDef="let element">
          <button mat-icon-button aria-label="expand row" (click)="(expandedElement = expandedElement === element ? null : element); $event.stopPropagation()">
            <mat-icon *ngIf="expandedElement !== element">keyboard_arrow_down</mat-icon>
            <mat-icon *ngIf="expandedElement === element">keyboard_arrow_up</mat-icon>
          </button>
        </td>
      </ng-container>

      <!-- Expanded Content Column - The detail row is made up of this one column that spans across all columns -->
      <ng-container matColumnDef="expandedDetail">
        <td mat-cell *matCellDef="let element" [attr.colspan]="columnsToDisplayWithExpand.length">
          <div class="example-element-detail"
               [@detailExpand]="element == expandedElement ? 'expanded' : 'collapsed'">
            <div class="example-element-diagram">
              <div class="example-element-position"> {{element.sexe}} </div>
              <div class="example-element-symbol"> {{element.profession}} </div>
              <div class="example-element-name"> {{element.tel}} </div>
            </div>
            <div class="example-element-description">
              {{element.nom}}
              <div *ngIf="authService.role.includes('BUREAU')"> <button mat-flat-button color="primary" (click)="reset(element)"> Reinitialiser le compte </button> </div>
            </div>
          </div>
        </td>
      </ng-container>

      <tr mat-header-row *matHeaderRowDef="columnsToDisplayWithExpand"></tr>
      <tr mat-row *matRowDef="let element; columns: columnsToDisplayWithExpand;"
          class="example-element-row"
          [class.example-expanded-row]="expandedElement === element"
          (click)="expandedElement = expandedElement === element ? null : element">
      </tr>
      <tr mat-row *matRowDef="let row; columns: ['expandedDetail']" class="example-detail-row"></tr>
    </table>

  </div>
</div>
