import {ChangeDetectionStrategy, ChangeDetectorRef, Component, DoCheck, OnInit} from '@angular/core';
import {AuthService} from "../../Services/auth.service";
import {MatDialog} from "@angular/material/dialog";
import {UserSaveComponent} from "../../dialogbox/user-save/user-save.component";
import {User} from "../../../Models/Entitys/User";
import {Membre} from "../../../Models/Entitys/Membre";
import {Observable, of} from "rxjs";

@Component({
  selector: 'app-profil',
  templateUrl: './profil.component.html',
  styleUrls: ['./profil.component.scss'],
  changeDetection: ChangeDetectionStrategy.OnPush
})
export class ProfilComponent implements DoCheck {

  born: string | Date ="";
  profession="";
  Contact="";
  Sexe="";
  CNI="";
  giveAt: string | Date ="";
  Parrain: string | null ="";
  nom="nom";

  membre:Membre|undefined;
  Residance="";
  username="";
  password="";
  user:User|undefined;

  memberObs$!:Observable<Membre>;

  constructor(public authService:AuthService, private dialogBox:MatDialog,
              private cd:ChangeDetectorRef) {
  }

  ngDoCheck() {
    this.loadData();
  }

  loadData(){
    if (this.authService.appUser){
      this.user=this.authService.appUser;
      let membre =this.authService.appUser.membre;
      this.nom = membre.nom;
      this.CNI = membre.cni;
      this.Sexe = membre.sexe;
      this.giveAt = membre.dcni;
      this.born = membre.ddn;
      this.Parrain = membre.p1;
      this.profession =membre.profession;
      this.Contact = membre.tel;
      this.Residance = membre.adresse
      this.membre = membre;

      this.memberObs$ = of(membre);

      this.cd.markForCheck();

    }
  }
  openDialogBox(){
    console.log(this.membre);
    this.dialogBox.open(UserSaveComponent,{
      // width:'60%',
      panelClass:'save--form',
      data:this.membre,
    })
  }
}
<div class="content">
  <div class="pan">
    <form  class="row--content">
      <mat-grid-list cols="1" rowHeight="40px" id="list1">

        <mat-grid-tile rowspan="5">
          <span class="card-img">
            <h2>{{nom}}</h2>
          </span>
        </mat-grid-tile>
        <mat-grid-tile>
          <label>User name: <span> {{user?.name}}</span></label>
        </mat-grid-tile>
<!--        <mat-grid-tile>-->
<!--          <label>Password: <span> {{user?.password}}</span></label>-->
<!--        </mat-grid-tile>-->

      </mat-grid-list>

      <mat-grid-list cols="1" rowHeight="40px" id="list2" *ngIf="memberObs$|async as memberObs">

        <mat-grid-tile>
          <label>Née le: <span>{{memberObs.ddn|date:'dd-MM-yyyy'}}</span></label>
        </mat-grid-tile>
        <mat-grid-tile>
          <label>Profession: <span>{{memberObs.profession}}</span></label>
        </mat-grid-tile>
        <mat-grid-tile>
          <label>Residance: <span>{{memberObs.adresse}}</span></label>
        </mat-grid-tile>
        <mat-grid-tile>
          <label>Contact: <span>{{memberObs.tel}}</span></label>
        </mat-grid-tile>
        <mat-grid-tile>
          <label>Sexe: <span>{{memberObs.sexe}}</span></label>
        </mat-grid-tile>
        <mat-grid-tile>
          <label>N° CNI: <span>{{memberObs.cni}}</span></label>
        </mat-grid-tile>
        <mat-grid-tile>
          <label>Délivrée le:<span>{{memberObs.dcni|date:'dd-MM-yyyy'}}</span></label>
        </mat-grid-tile>
        <mat-grid-tile>
          <label>Parrain: <span>{{memberObs.p1}}</span></label>
        </mat-grid-tile>

      </mat-grid-list>

<!--      <mat-grid-list cols="1" rowHeight="50px">-->


<!--      </mat-grid-list>-->
    </form >
    <div class="options">
      <button mat-raised-button (click)="openDialogBox()">Modifier</button>
    </div>
  </div>
</div>
