import {Component, DoCheck, OnInit} from '@angular/core';
import {Participation} from "../../../Models/Entitys/Participation";
import {Beneficiaire} from "../../../Models/Entitys/Beneficiaire";
import {Tontine} from "../../../Models/Abstracts/Tontine";
import {Cotisation} from "../../../Models/Entitys/Cotisation";
import {TontineServiceService} from "../../Services/tontine-service.service";
import {ActivatedRoute} from "@angular/router";
import jsPDF from "jspdf";
import autoTable from "jspdf-autotable";
import {DataService} from "../../Services/data.service";

@Component({
  selector: 'app-cotisation-detail',
  templateUrl: './cotisation-detail.component.html',
  styleUrls: ['./cotisation-detail.component.scss']
})
export class CotisationDetailComponent implements OnInit,DoCheck{


  displayedColumns: string[] = ['position', 'name', 'weight', 'symbol', 'echec'];
  dataSource:Participation[]=[];
  benefs:Map<number,Beneficiaire> = new Map<number, Beneficiaire>();
  idCot!:number;
  idTontine!:number;
  tontine:Tontine|undefined;
  cot!:Cotisation;
  isEnd:string="No";
  tauxTontine=0;
  tauxEchec=0;
  tauxRetard=0;
  total:number=0;
  totalExpet:number=0;
  totalEchec:number=0;
  totalRetard:number=0;
  nbE=0;
  nbR=0;
  type: boolean = false;

  constructor(private tontineService:TontineServiceService,
              private route:ActivatedRoute,private dataService:DataService) {

  }
  ngOnInit() {

    this.idCot=this.route.snapshot.params["id"];
    this.idTontine=this.route.snapshot.params["idTon"];
    this.tontine=this.dataService.actualTontine;
    this.cot=this.dataService.actualCotisation;


    this.tontineService.getParticipation(this.idCot).subscribe(
      data=>{
        this.dataSource = data;
      }
    );
    this.tontineService.getAllLastbenef(this.idCot).subscribe(
      data=>{
        for (let ben of data){
          if(ben.enregistrement)
          this.benefs.set(ben.enregistrement.id,ben);
        }
      }
    );

  }

  ngDoCheck() {
        this.tontine?.actif? this.isEnd="No":this.isEnd="finish";
        this.tauxTontine=Number(this.tontine?.tauxTontine);
        this.tauxEchec=Number(this.tontine?.tauxEchec);
        this.tauxRetard=Number(this.tontine?.tauxRetard);


    this.setTotal();
    this.setType();
  }

  getclass(id:number):string{
    if(this.cot.beneficiaire?.enregistrement)
    if(this.benefs.has(id) && this.cot.beneficiaire?.enregistrement.id!=id)
      return "ben";
    return "nonBen";
  }

  setTotal(){

    this.total=0;
    this.totalEchec=0;
    this.totalRetard=0;
    this.totalExpet=0;
    this.nbE=0;
    this.nbR=0;

    for(let data of this.dataSource ){
      if (data.tontine){
        this.total+=this.tauxTontine;
        if(data.retard){
          this.totalRetard+=(this.cot.tauxEchec+this.cot.tauxTontine);
          this.nbR++;
        }
      }
      else {
        this.totalEchec+=(this.cot.tauxEchec+this.cot.tauxTontine);
        this.nbE++;
      }
      this.totalExpet+=this.tauxTontine;
    }
  }

  setType(){
    if (this.tontine?.type.code==1)
      this.type= true;
    else
      this.type=false;
  }


  public openPDF(): void {

    var part=[];
    let i =0;
    for(let element of this.dataSource){
      i++;
      var tempObj = {"N.": String(i),
        "Nom":element.enregistrement.membre.nom,
        "Tontine":element.tontine?"OUI":"NON",
        "Retard":element.retard?"OUI":"NON",
        "Echec":element.echec?"OUI":"NON",
      };

      part.push(tempObj);
    }
    const options = {
      startY: 20, // Vertical position to start the table (in mm)
      startX:20,
    };
    const doc = new jsPDF();
    if (this.cot.createdAt)
      doc.text("Tontine : "+String(this.tontine?.nom).slice(16)+"       Le : "+this.cot.createdAt.toString(), 10, 7);
    if(this.tontine?.type.id==1)
      doc.text("Total de cotisation : "+this.total+"/"+this.totalExpet+" Fcfa    taux de vente:"+this.cot.tauxVente+" Fcfa", 10, 21);
    else
      doc.text("Total de cotisation : "+this.total+"/"+this.totalExpet+" Fcfa", 10, 21);

    doc.text("Beneficier : "+this.cot.beneficiaire?.enregistrement?.membre.nom+"        Montant : "+this.cot.beneficiaire?.tauxRemis+" Fcfa", 10,28);
    doc.text("Avalise : "+this.cot.beneficiaire?.avaliste?.membre.nom+"     Suplement : "+this.cot.supplement+" Fcfa", 10,35);
    doc.text("Echec : "+this.nbE+"   ("+this.totalEchec+" Fcfa)     "+"Retard : "+this.nbR+"   ("+this.totalRetard +" Fcfa)     ", 10,42);

    autoTable(doc,{html:'#htmlData',startY:49});


    doc.save('Cotisation '+this.cot.nom+'.pdf');
  }

}

<div class="content">
  <div class="tab" id="{{isEnd}}">
    <span class="pan"  >
      <h4> {{cot.nom}} </h4>
      <h1>SEANCE {{cot.rang}}</h1>
    </span>
    <table mat-table [dataSource]="dataSource" class="{{isEnd}}" id="htmlData">

      <!--- Note that these columns can be defined in any order.
            The actual rendered columns are set as a property on the row definition" -->

      <!-- Position Column -->
      <ng-container matColumnDef="position">
        <th mat-header-cell *matHeaderCellDef> No. </th>
        <td mat-cell *matCellDef="let element,let i = index" class="{{getclass(element.enregistrement.id)}} {{element.enregistrement.rang|enNumber}}">
          <span class="mobile-label"> No. :</span>
          {{i+1}}
        </td>
      </ng-container>

      <!-- Name Column -->
      <ng-container matColumnDef="name">
        <th mat-header-cell *matHeaderCellDef> Nom </th>
        <td mat-cell *matCellDef="let element" class="{{getclass(element.enregistrement.id)}}">
          <span class="mobile-label"> Nom :</span>
          {{element.enregistrement.membre.nom}} {{element.enregistrement.rang|enNumber}} </td>
      </ng-container>

      <!-- Weight Column -->
      <ng-container matColumnDef="weight">
        <th mat-header-cell *matHeaderCellDef> Cotisation ({{cot.tauxTontine|money}}) </th>
        <td mat-cell *matCellDef="let element" class="{{getclass(element.enregistrement.id)}}">
          <span class="mobile-label"> Cotisation :</span>

          <span *ngIf="!element.tontine">Non</span>
          <span *ngIf="element.tontine" class="tonine"> Oui </span>
        </td>
      </ng-container>

      <!-- Symbol Column -->
      <ng-container matColumnDef="symbol">
        <th mat-header-cell *matHeaderCellDef> Retard ({{tauxRetard|money}})</th>
        <td mat-cell *matCellDef="let element" class="{{getclass(element.enregistrement.id)}}">
          <span class="mobile-label"> Retard :</span>
          <span *ngIf="element.retard" class="tonine"> Oui </span>
          <span *ngIf="!element.retard" class="retard"> Non </span>
        </td>
      </ng-container>

      <ng-container matColumnDef="echec">
        <th mat-header-cell *matHeaderCellDef> Echec ({{cot.tauxEchec|money}})</th>
        <td mat-cell *matCellDef="let element" class="{{getclass(element.enregistrement.id)}}">
          <span class="mobile-label"> Echec :</span>
          <span *ngIf="element.echec" class="tonine"> Oui </span>
          <span *ngIf="!element.echec" class="echec"> Non </span>
        </td>
      </ng-container>

      <tr mat-header-row *matHeaderRowDef="displayedColumns"></tr>
      <tr mat-row *matRowDef="let row; columns: displayedColumns;"></tr>

    </table>
    <div class="pan" id="bot">

      <mat-grid-list cols="4" rowHeight="45px" class="pc--mat--grid--list">
        <mat-grid-tile>
          Total : {{total|money}} /{{totalExpet|money}}
        </mat-grid-tile>
        <mat-grid-tile>
          Retard : {{totalRetard|money}} ( {{nbR}} )
        </mat-grid-tile>
        <mat-grid-tile>
          Echec: {{totalEchec|money}} ( {{nbE}} )
        </mat-grid-tile>
        <mat-grid-tile>
          Taux Vente: {{cot.tauxVente|money}}
        </mat-grid-tile>
        <mat-grid-tile>
           Bénéficiaire: {{cot.beneficiaire?.enregistrement?.membre?.nom}}
        </mat-grid-tile>
        <mat-grid-tile>
          Avalise: {{cot.beneficiaire?.avaliste?.membre?.nom}}
        </mat-grid-tile>
        <mat-grid-tile *ngIf="tontine?.type?.code==1">
          Supplement : {{cot.supplement|money}}
        </mat-grid-tile>
        <mat-grid-tile *ngIf="tontine?.type?.code==1">
          Montant beneficier: {{cot.beneficiaire?.tauxRemis}}
        </mat-grid-tile>
        <mat-grid-tile colspan="4">
          <button mat-flat-button class="btn-imprime" (click)="openPDF()">IMPRIMER </button>
        </mat-grid-tile>

      </mat-grid-list>

      <mat-grid-list cols="1" rowHeight="45px" class="mobile--mat--grid--list">
        <mat-grid-tile>
          Total : {{total|money}} /{{totalExpet|money}}
        </mat-grid-tile>
        <mat-grid-tile>
          Retard : {{totalRetard|money}} ( {{nbR}} )
        </mat-grid-tile>
        <mat-grid-tile>
          Echec: {{totalEchec|money}} ( {{nbE}} )
        </mat-grid-tile>
        <mat-grid-tile>
          Taux Vente: {{cot.tauxVente|money}}
        </mat-grid-tile>
        <mat-grid-tile>
          Bénéficiaire: {{cot.beneficiaire?.enregistrement?.membre?.nom}}
        </mat-grid-tile>
        <mat-grid-tile>
          Avalise: {{cot.beneficiaire?.avaliste?.membre?.nom}}
        </mat-grid-tile>
        <mat-grid-tile *ngIf="tontine?.type?.code==1">
          Supplement : {{cot.supplement|money}}
        </mat-grid-tile>
        <mat-grid-tile *ngIf="tontine?.type?.code==1">
          Montant beneficier: {{cot.beneficiaire?.tauxRemis}}
        </mat-grid-tile>
        <mat-grid-tile>
          <button mat-flat-button class="btn-imprime" (click)="openPDF()">IMPRIMER </button>
        </mat-grid-tile>

      </mat-grid-list>
    </div>

  </div>
</div>
