import {Component, OnDestroy, OnInit} from '@angular/core';
import {Participation} from "../../../../../Models/Entitys/Participation";
import {BilanServiceService} from "../../../../Services/bilan-service.service";
import {ActivatedRoute} from "@angular/router";
import {Enregistrement} from "../../../../../Models/Entitys/Enregistrement";
import {Subscription} from "rxjs";

@Component({
  selector: 'app-classement',
  templateUrl: './classement.component.html',
  styleUrls: ['./classement.component.scss']
})

export class ClassementComponent implements OnInit,OnDestroy{

  parts:Participation[]=[];
  displayedColumns: string[] = ['position', 'name', 'E.N.P', 'echecs','affiche'];
  dataSource:Enregistrement[]=[];
  tab:Array<Enregistrement[]>=[];
  map:Map<number,Participation[]>= new Map<number, Participation[]>();
  idTontine=0;
  keys:number[]=[];
  index=0;
  PartsCall:Subscription = new Subscription();
  liste:number[][]=[];

  constructor(private bilanService:BilanServiceService,private router:ActivatedRoute) {
  }

  ngOnInit() {
    this.idTontine=Number(this.router.snapshot.params["id"]);
    console.log("id",this.idTontine);

    this.PartsCall=this.bilanService.getParts(this.idTontine).subscribe(
      data=>{
        this.parts=data;

        let key:number;

        for(let part of this.parts){
          key=part.enregistrement.id
          if(!this.map.has(key)){
            this.map.set(key,[part]);
          }
          else {
            let parts= this.map.get(key);
            if(parts){
              parts.push(part);
              this.map.set(key,parts);
            }
          }
        }
        this.dispatchData();
      }
    );

  }
ngOnDestroy() {
    this.PartsCall.unsubscribe();
}

  dispatchData(){

    let nbEchecs=0;
    let nbRetard=0;
    let nbEchecsPaid=0;
    let dette:number =0;

    for (let key of this.map.keys()){


      let parts=this.map.get(key);
      if(parts){
        nbEchecs=0;
        nbEchecsPaid=0;
        nbRetard=0;
        dette=0;

        for (let part of parts){
          let cotDate=part.cotisation.createdAt;
          let enrgDate =part.enregistrement.createdAt;
          if(cotDate && enrgDate){
            if(!part.tontine){
              nbEchecs++;
            }
            if (!part.tontine && !part.echec && enrgDate<cotDate){
              nbEchecsPaid++;
            }
            if (part.retard){
              nbRetard++;
            }

          }
        }
        dette = nbEchecs-nbEchecsPaid;

        this.liste.push([key,nbEchecs,dette]);

      }

    }

    this.liste.sort((a,b)=>{
      if (a[2] !== b[2]) {
        return a[2] - b[2]; // Tri par dette croissante
      } else {
        return a[1] - b[1]; // Si les dettes sont égales, trie par nbEchecs croissants
      }
    });
    let alphaList:Enregistrement[]=[];
    let nonAlphaList:Enregistrement[]=[];
    for (let det of this.liste){
      let enrg=this.map.get(det[0])
      if(enrg){
        if (det[2]==0)
          alphaList.push(enrg[0].enregistrement);
        else
        nonAlphaList.push(enrg[0].enregistrement);
      }

    }

    this.dataSource=(alphaList.sort()).concat(nonAlphaList);


  }

}
<div class="content">
  <div class="tab">
    <table mat-table [dataSource]="dataSource.slice()" class="mat-elevation-z8">

      <!--- Note that these columns can be defined in any order.
            The actual rendered columns are set as a property on the row definition" -->

      <!-- Position Column -->
      <ng-container matColumnDef="position">
        <th mat-header-cell *matHeaderCellDef> No. </th>
        <td mat-cell *matCellDef="let element,let i = index"> {{i+1}} </td>
      </ng-container>

      <!-- Name Column -->
      <ng-container matColumnDef="name">
        <th mat-header-cell *matHeaderCellDef> Name </th>
        <td mat-cell *matCellDef="let element"> {{element.membre.nom }}{{element.rang|enNumber }} </td>
      </ng-container>

      <ng-container matColumnDef="E.N.P">
        <th mat-header-cell *matHeaderCellDef> E.N.P </th>
        <td mat-cell *matCellDef="let element,let i=index"> {{liste[i][2]}} </td>
      </ng-container>

      <!-- Weight Column -->
      <ng-container matColumnDef="echecs">
        <th mat-header-cell *matHeaderCellDef> echecs </th>
        <td mat-cell *matCellDef="let element,let i=index"> {{liste[i][1]}} </td>
      </ng-container>

        <ng-container matColumnDef="affiche">
            <th mat-header-cell *matHeaderCellDef></th>
            <td mat-cell *matCellDef="let element,let i=index">
                <button mat-stroked-button color="primary" routerLink="public/tontine/desc/membre/{{i}}"> Afficher </button>
            </td>
        </ng-container>



      <tr mat-header-row *matHeaderRowDef="displayedColumns"></tr>
      <tr mat-row *matRowDef="let row; columns: displayedColumns;"></tr>
    </table>
  </div>
</div>
