import { Component } from '@angular/core';
import {MatDialogModule} from "@angular/material/dialog";
import {MatButtonModule} from "@angular/material/button";
import {MatInputModule} from "@angular/material/input";
import {FormBuilder, FormControl, ReactiveFormsModule} from "@angular/forms";
import {MatFormFieldModule} from "@angular/material/form-field";

@Component({
  selector: 'app-buy-pret-dialog',
  templateUrl: './buy-pret-dialog.component.html',
  standalone: true,
  imports: [
    MatDialogModule,
    MatButtonModule,
    MatInputModule,
    ReactiveFormsModule
  ],
  styleUrls: ['./buy-pret-dialog.component.scss']
})
export class BuyPretDialogComponent {
  form =this.fb.group({
    montant:new FormControl(0)
  })


  constructor(private fb:FormBuilder) {
  }

}
<form [formGroup]="form">
<div mat-dialog-content>

    Confirmer le Versement de Chez Mr
    <p>
<!--        {{participation.enregistrement.membre.nom}}-->
    </p>
    <p>
<!--        NB: le retard sera pris en compte.-->
    </p>

        <mat-form-field appearance="outline">
            <mat-label>Montant</mat-label>
            <input type="text" placeholder="Montant" formControlName="montant">
        </mat-form-field>

</div>
<div mat-dialog-actions>
    <button type="submit" mat-flat-button cdkFocusInitial  mat-button  class="Btn" color="primary">OUI</button>
    <button mat-button mat-dialog-close color="accent" class="Btn">NON</button>
</div>
</form>
