      if (this.tontine.type.code == 1) {
        if (total > this.tontine.nbSeances) {
          this.supl = (total - this.tontine.nbSeances) * this.tontine?.tauxTontine;
          this.benefMontant = this.tontine.nbSeances * this.tontine?.tauxTontine;
